<template>
	<div>
		<div class="checkall">
			<div :class="{'check-select':true,'check-seled':isble}" @click="checAll"></div>
			<div class="check-title">海密严选</div>
		</div>
		<checkRdio></checkRdio>
		<jisuan ></jisuan>
		<footerNav></footerNav>
	</div>
</template>
<script>
import checkRdio from './component/Radios'
import jisuan from './component/jisuan'
import footerNav from '@/components/footerNav'
import { mapGetters, mapActions} from 'vuex'

export default {
	methods:{
		...mapActions(['checAll'])
	},
	computed: {
		...mapGetters(['isble'])
	},
	components: {
		checkRdio,
		jisuan,
		footerNav
	},
	created () {
		var list = window.localStorage['list'];
		this.lists = JSON.parse(list)
		this.lists.forEach(function(val){
			val['id'] = false
		})	
		this.$store.state.list = this.lists;
	}
}
</script>
